import { Box, Button, Dialog, DialogActions, DialogContent, DialogTitle, FormControl, FormHelperText, IconButton, InputAdornment, InputLabel, MenuItem, Stack, TextField } from "@mui/material"
import './header.css'
import Select from "react-select";
import SearchIcon from '@mui/icons-material/Search';
import UploadIcon from '@mui/icons-material/Upload';
import CloseIcon from '@mui/icons-material/Close';
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getRecipes } from "../features/recipeSlice";
import { config } from "../App";
import { changeDurationOption, changeOption, getTitle } from "../features/filterSlice";
const Header = () => {
  let data = {
    title: "",
    ingredients: "",
    steps: "",
    calories: "",
    prepTime: "",
    image: "",
    foodType:"",
    mealType:"",
    servings:"",
  };
  const { meal } = useSelector((state) => state.filter);
  const { duration } = useSelector((state) => state.filter)
  const { recipes } = useSelector((state) => state.recipe);
  const [searchKey, setSearchKey] = useState("");
  const [value, setValue] = useState(data);
  const [open, setOpen] = useState(false);
  const [image, setImage] = useState({ preview: '', data: '' })
  const [addresses, setAddresses] = useState({ all: [], selected: "" });
  const [newAddress, setNewAddress] = useState({
    isAddingNewAddress: false,
    value: "",
  });

  const dispatch = useDispatch();

  let options = [{ value: "breakfast", label: "Breakfast" },
  { value: "lunch", label: "Lunch" },
  { value: "dinner", label: "Dinner" }]

  let optionsDuration = [
    { value: "0-30", label: "Within 30 min" },
    { value: "0-60", label: "Within 1 Hour" },

  ]
  const getRecipe = async () => {
    let response = {};
    let errored = false;

    try {
      response = await (
        await fetch(`${config.endpoint}/recipe`, {
          method: "GET",
        })
      ).json();
    } catch (e) {
      errored = true;
      return errored;
    }

    dispatch(getRecipes(response))
    return recipes
  };


  const handleClear = async () => {
    dispatch(changeOption([]))
    if (typeof duration !== 'undefined') {

      if (duration.length !== 0) {
        performDurationFilter(duration)
      }
      if (duration.length == 0) {
        return await getRecipe()
      }
    }
  };

  const handleClearDuration = async () => {
    dispatch(changeDurationOption([]))
    if (typeof meal !== 'undefined') {

      if (meal.length !== 0) {
        performMealFilter(meal)
      }
      if (meal.length == 0) {
        return await getRecipe()
      }
    }
  };

  const performMealFilter = async (e, text) => {

    dispatch(changeOption(e))
    let response = {};
    let errored = false;

    try {
      response = await (
        await fetch(`${config.endpoint}/recipe?meal=${e.value}`, {
          method: "POST",
        })
      ).json();
    } catch (e) {
      errored = true;
      return errored;
    }
    dispatch(getRecipes(response));
  };

  const performDurationFilter = async (e, text) => {
    dispatch(changeDurationOption(e))
    let response = {};
    let errored = false;

    if (meal.length === 0) {
      try {
        response = await (
          await fetch(`${config.endpoint}/recipe?duration=${e.value}`, {
            method: "POST",
          })
        ).json();
      } catch (e) {
        errored = true;
        return errored;
      }
      dispatch(getRecipes(response));
    }
    else {
      performSorting()
    }
  };

  const performSorting = async () => {

    let response = {};
    let errored = false;

    try {
      response = await (
        await fetch(`${config.endpoint}/recipe?meal=${meal.value}&duration=${duration.value}`, {
          method: "POST",
        })
      ).json();
    } catch (e) {
      errored = true;
      return errored;
    }

    dispatch(getRecipes(response));

  };

  useEffect(() => {
    if (meal.length !== 0 && duration.length !== 0) {
      performSorting()
    }
  }, [meal, duration])

  const performSearch = async (text) => {
    let value = [];
    let errored = false;
    let response = {};
    setSearchKey(text);

    try {
      response = await (
        await fetch(`${config.endpoint}/recipe?title=${text}`, {
          method: "POST",
        })
      ).json();
      dispatch(getRecipes(response));
      return recipes
    } catch (error) {
      errored = true;
      return errored;
    }
  };

  const debounceSearch = (event, debounceTimeout) => {
    let targetvalue = event.target.value;

    if (debounceTimeout) {
      clearTimeout(debounceTimeout);
    }
    debounceTimeout = setTimeout(() => performSearch(targetvalue), 500);
  };

  const search = (e) => {
    dispatch(getTitle(e.target.value));
    debounceSearch(e);
  };

  const handleClickOpen = () => {
    setOpen(true);
  };
  const handleChange = (e) => {
    console.log(e)
    const { name, value } = e.target;

    setValue((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleClose = async () => {
    setOpen(false);
    let response = {};
    let errored = false;
    try {
      response = await fetch(`${config.endpoint}/recipe`, {
        method: "POST",
        body: JSON.stringify({
        
          title: value.title,
          ingredients: value.ingredients,
          steps: value.steps,
          calories:value.calories,
          prepTime: value.prepTime,
          image:value.image,
          foodType: value.foodType,
          mealType:value.mealType,
          servings:value.servings,
        }),
      }).json();
    } catch (e) {
      errored = true;
      return errored;
    }
    setValue(response);
    return value;
  };
  const handleFileChange = (e) => {
    const img = {
      preview: URL.createObjectURL(e.target.files[0]),
      data: e.target.files[0],
    }
   
    setImage(img)
  }

  const addAddress = async (token, newAddress) => {
    // console.log("new",newAddress.value)
     try {
       // TODO: CRIO_TASK_MODULE_CHECKOUT - Add new address to the backend and display the latest list of addresses
       const headers={
         Authorization: `Bearer ${token}`,
         'Content-Type': 'application/json'
       }
       const addressdata={
         address:newAddress.value
       }
      let response = await fetch(`${config.endpoint}/user/addresses`,addressdata,{headers})
      // console.log(response.data)
       setAddresses({ ...addresses, all: response.data });
       
      // setNewAddress(" ")
       //return response.data;
       if(response){
         newAddress=''
         setNewAddress({
           isAddingNewAddress: false,
           value: "",
         })
         //  console.log(newAddress)
       }
       } catch (e) {
       if (e.response) {
         enqueueSnackbar(e.response.data.message, { variant: "error" });
       } else {
         enqueueSnackbar(
           "Could not add this address. Check that the backend is running, reachable and returns valid JSON.",
           {
             variant: "error",
           }
         );
       }
     }
   };

  const AddNewAddressView = ({
    token,
    newAddress,
    handleNewAddress,
    addAddress,
  }) => {
    return (
      <Box display="flex" flexDirection="column">
        <TextField
          multiline
          minRows={4}
          placeholder="Enter your complete address"
          onChange={(e) => {
            handleNewAddress({isAddingNewAddress: true,
              value: e.target.value});
          }}
         value={newAddress.value}
        />
        <Stack direction="row" my="1rem">
          <Button
            variant="contained"
            onClick={()=>{
              addAddress(token,newAddress)
             // console.log(e.target.value)
             
            }}
            >
            Add
          </Button>
          <Button
            variant="text"
            onClick={()=>{
             handleNewAddress((currNewAddress) => ({
                ...currNewAddress,
                isAddingNewAddress: false,
              }))
            }}>
            Cancel
          </Button>
        </Stack>
      </Box>
    );
  };
  
  return (
    <Box>
      <Box className="filter-btn-box">

        <div className="filter-bar">
          <div className="filter-bar-tile">
            <label htmlFor="duration">Filters:</label>
          </div>

          <div className="filter-bar-tile">

            <Select value={meal} options={options} onChange={(e) => performMealFilter(e, meal)} placeholder="Meal Type" styles={{ menu: provided => ({ ...provided, zIndex: 7 }) }} />

            <div className="ms-3" style={{ color: "#b13d2f", cursor: "pointer" }} onClick={handleClear}>
              Clear
            </div>
          </div>

          <div className="filter-bar-tile">
            <Select value={duration} options={optionsDuration} onChange={(e) => performDurationFilter(e, duration)} placeholder="Duration" styles={{ menu: provided => ({ ...provided, zIndex: 7 }) }} />
            <div className="ms-3" style={{ color: "#b13d2f", cursor: "pointer" }} onClick={handleClearDuration}>
              Clear
            </div>
          </div>
          <Stack className="search-field" style={{ padding: '0px 20px' }}>
            <TextField
              className="search-desktop"
              size="small"
              style={{ width: '120px' }}
              fullWidth
              InputProps={{
                endAdornment: (
                  <InputAdornment position="start">
                    <SearchIcon className="search-bar" color="primary" />
                  </InputAdornment>
                ),
              }}
              placeholder="Search"
              name="search"
              onChange={search}
            />
          </Stack>
        </div>

        <Stack direction="row" spacing={2}>
          <Button
            className="upload-button"
            startIcon={<UploadIcon />}
            variant="contained"
            onClick={handleClickOpen}
            id="upload-btn"
          >
            Upload
          </Button>
          <Box>
            <Dialog open={open} onClose={handleClose}>
              <DialogTitle className="dialog">
                Upload Recipe
                <IconButton
                  aria-label="close"
                
                  onClick={handleClose}
                  sx={{
                    position: "absolute",
                    right: 8,
                    top: 8,
                    color: (theme) => theme.palette.grey[500],
                  }}
                >
                  <CloseIcon />
                </IconButton>
              </DialogTitle>
              <DialogContent className="dialog">
                <TextField
                  autoFocus
                  margin="dense"
                  id="title"
                  label="Title"
                  type="text"
                  helperText="This Link will be used to derive the video"
                  fullWidth
                  name="title"
                  variant="outlined"
                  value={value.title}
                  onChange={handleChange}
                />
                <TextField
                  autoFocus
                  margin="dense"
                  id="ingredients"
                  label="Ingredients"
                  type="text"
                  helperText="This link will be used to preview the thumbnail image"
                  fullWidth
                  variant="outlined"
                  name="ingredients"
                  value={value.ingredients}
                  onChange={handleChange}
                />
                  {newAddress.isAddingNewAddress===false?
            <Button
                color="primary"
                variant="contained"
                id="add-new-btn"
                size="large"
                onClick={() => {
                  setNewAddress((currNewAddress) => ({
                    ...currNewAddress,
                    isAddingNewAddress: true,
                  }));
                }}
              >
                Add new address
            </Button>
            :
            <AddNewAddressView
                token={token}
                newAddress={newAddress}
                handleNewAddress={setNewAddress}
                addAddress={addAddress}
            />
          }
                <TextField
                  autoFocus
                  margin="dense"
                  id="steps"
                  label="Steps"
                  type="text"
                  helperText="The title will be representative text of video"
                  fullWidth
                  variant="outlined"
                  name="steps"
                  value={value.steps}
                  onChange={handleChange}
                />
                 <TextField
                  autoFocus
                  margin="dense"
                  id="calories"
                  label="Calories"
                  type="text"
                  helperText="The title will be representative text of video"
                  fullWidth
                  variant="outlined"
                  name="calories"
                  value={value.calories}
                  onChange={handleChange}
                />

                <TextField
                  autoFocus
                  margin="dense"
                  id="prepTime"
                  label="Preparation Time"
                  type="text"
                  helperText="The title will be representative text of video"
                  fullWidth
                  variant="outlined"
                  name="prepTime"
                  value={value.prepTime}
                  onChange={handleChange}
                />

            
<input type='file' name='file' onChange={handleFileChange} className="image-upload"></input>
<FormHelperText style={{margin: '1px 1px 10px 10px'}}>This is</FormHelperText>
        {/* {image.preview && <img src={image.preview} alt="Uploaded Image" height="300" />} */}
            <Select value={value.mealType} options={options} onChange={(e)=>setValue({mealType:e})} placeholder="Meal Type" styles={{ menu: provided => ({ ...provided, zIndex: 7 }) }} />
              <FormHelperText style={{margin: '1px 1px 1px 10px'}}>This is</FormHelperText>
            <TextField
                  autoFocus
                  margin="dense"
                  id="servings"
                  label="Servings"
                  type="text"
                  helperText="The title will be representative text of video"
                  fullWidth
                  variant="outlined"
                  name="servings"
                  value={value.servings}
                  onChange={handleChange}
                />

              </DialogContent>
              <DialogActions className="dialog">
                <Button className="cancel-button" id="upload-btn-cancel" onClick={handleClose}>
                  Cancel
                </Button>
                <Button
                  className="submit-button"
                  id="upload-btn-submit"
                  variant="contained"
                  onClick={handleClose}
                >
                  Upload recipe
                </Button>
              </DialogActions>
            </Dialog>
          </Box>
        </Stack>
      </Box>
    </Box>
  )
}

const AddNewAddressView = ({
  token,
  newAddress,
  handleNewAddress,
  addAddress,
}) => {
  return (
    <Box display="flex" flexDirection="column">
      <TextField
        multiline
        minRows={4}
        placeholder="Enter your complete address"
        onChange={(e) => {
          handleNewAddress({isAddingNewAddress: true,
            value: e.target.value});
        }}
       value={newAddress.value}
      />
      <Stack direction="row" my="1rem">
        <Button
          variant="contained"
          onClick={()=>{
            addAddress(token,newAddress)
           // console.log(e.target.value)
           
          }}
          >
          Add
        </Button>
        <Button
          variant="text"
          onClick={()=>{
           handleNewAddress((currNewAddress) => ({
              ...currNewAddress,
              isAddingNewAddress: false,
            }))
          }}>
          Cancel
        </Button>
      </Stack>
    </Box>
  );
};

export default Header
