import { Box, Button, Dialog, DialogActions, DialogContent, DialogTitle, FormControl, FormHelperText, IconButton, InputAdornment, InputLabel, MenuItem, Stack, TextField } from "@mui/material"
import './header.css'
import Select from "react-select";
import SearchIcon from '@mui/icons-material/Search';
import UploadIcon from '@mui/icons-material/Upload';
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getRecipes } from "../features/recipeSlice";
import { config } from "../App";
import { changeDurationOption, changeOption, getTitle } from "../features/filterSlice";
const Header = () => {
  let data = {
    title: "",
    ingredients: "",
    steps: "",
    calories: "",
    prepTime: "",
    image: "",
    foodType:"",
    mealType:"",
    servings:"",
  };
  const { meal } = useSelector((state) => state.filter);
  const { duration } = useSelector((state) => state.filter)
  const { recipes } = useSelector((state) => state.recipe);
  const [searchKey, setSearchKey] = useState("");
  const [value, setValue] = useState(data);

  const dispatch = useDispatch();

  let options = [{ value: "breakfast", label: "Breakfast" },
  { value: "lunch", label: "Lunch" },
  { value: "dinner", label: "Dinner" }]

  let optionsDuration = [
    { value: "0-30", label: "Within 30 min" },
    { value: "0-60", label: "Within 1 Hour" },

  ]
  const getRecipe = async () => {
    let response = {};
    let errored = false;

    try {
      response = await (
        await fetch(`${config.endpoint}/recipe`, {
          method: "GET",
        })
      ).json();
    } catch (e) {
      errored = true;
      return errored;
    }

    dispatch(getRecipes(response))
    return recipes
  };


  const handleClear = async () => {
    dispatch(changeOption([]))
    if (typeof duration !== 'undefined') {

      if (duration.length !== 0) {
        performDurationFilter(duration)
      }
      if (duration.length == 0) {
        return await getRecipe()
      }
    }
  };

  const handleClearDuration = async () => {
    dispatch(changeDurationOption([]))
    if (typeof meal !== 'undefined') {

      if (meal.length !== 0) {
        performMealFilter(meal)
      }
      if (meal.length == 0) {
        return await getRecipe()
      }
    }
  };

  const performMealFilter = async (e, text) => {

    dispatch(changeOption(e))
    let response = {};
    let errored = false;

    try {
      response = await (
        await fetch(`${config.endpoint}/recipe?meal=${e.value}`, {
          method: "POST",
        })
      ).json();
    } catch (e) {
      errored = true;
      return errored;
    }
    dispatch(getRecipes(response));
  };

  const performDurationFilter = async (e, text) => {
    dispatch(changeDurationOption(e))
    let response = {};
    let errored = false;

    if (meal.length === 0) {
      try {
        response = await (
          await fetch(`${config.endpoint}/recipe?duration=${e.value}`, {
            method: "POST",
          })
        ).json();
      } catch (e) {
        errored = true;
        return errored;
      }
      dispatch(getRecipes(response));
    }
    else {
      performSorting()
    }
  };

  const performSorting = async () => {

    let response = {};
    let errored = false;

    try {
      response = await (
        await fetch(`${config.endpoint}/recipe?meal=${meal.value}&duration=${duration.value}`, {
          method: "POST",
        })
      ).json();
    } catch (e) {
      errored = true;
      return errored;
    }

    dispatch(getRecipes(response));

  };

  useEffect(() => {
    if (meal.length !== 0 && duration.length !== 0) {
      performSorting()
    }
  }, [meal, duration])

  const performSearch = async (text) => {
    let value = [];
    let errored = false;
    let response = {};
    setSearchKey(text);

    try {
      response = await (
        await fetch(`${config.endpoint}/recipe?title=${text}`, {
          method: "POST",
        })
      ).json();
      dispatch(getRecipes(response));
      return recipes
    } catch (error) {
      errored = true;
      return errored;
    }
  };

  const debounceSearch = (event, debounceTimeout) => {
    let targetvalue = event.target.value;

    if (debounceTimeout) {
      clearTimeout(debounceTimeout);
    }
    debounceTimeout = setTimeout(() => performSearch(targetvalue), 500);
  };

  const search = (e) => {
    dispatch(getTitle(e.target.value));
    debounceSearch(e);
  };

  const handleClickOpen = () => {
    setOpen(true);
  };
  const handleChange = (e) => {
    const { name, value } = e.target;

    setValue((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleClose = async () => {
    setOpen(false);
    let response = {};
    let errored = false;
    try {
      response = await fetch(`${config.endpoint}/recipe`, {
        method: "POST",
        body: JSON.stringify({
        
          title: value.title,
          ingredients: value.ingredients,
          steps: value.steps,
          calories:value.calories,
          prepTime: value.prepTime,
          image:value.image,
          foodType: value.foodType,
          mealType:value.mealType,
          servings:value.servings,
        }),
      }).json();
    } catch (e) {
      errored = true;
      return errored;
    }
    setValue(response);
    return value;
  };

  return (
    <Box>
      <Box className="filter-btn-box">

        <div className="filter-bar">
          <div className="filter-bar-tile">
            <label htmlFor="duration">Filters:</label>
          </div>

          <div className="filter-bar-tile">

            <Select value={meal} options={options} onChange={(e) => performMealFilter(e, meal)} placeholder="Meal Type" styles={{ menu: provided => ({ ...provided, zIndex: 7 }) }} />

            <div className="ms-3" style={{ color: "#b13d2f", cursor: "pointer" }} onClick={handleClear}>
              Clear
            </div>
          </div>

          <div className="filter-bar-tile">
            <Select value={duration} options={optionsDuration} onChange={(e) => performDurationFilter(e, duration)} placeholder="Duration" styles={{ menu: provided => ({ ...provided, zIndex: 7 }) }} />
            <div className="ms-3" style={{ color: "#b13d2f", cursor: "pointer" }} onClick={handleClearDuration}>
              Clear
            </div>
          </div>
          <Stack className="search-field" style={{ padding: '0px 20px' }}>
            <TextField
              className="search-desktop"
              size="small"
              style={{ width: '120px' }}
              fullWidth
              InputProps={{
                endAdornment: (
                  <InputAdornment position="start">
                    <SearchIcon className="search-bar" color="primary" />
                  </InputAdornment>
                ),
              }}
              placeholder="Search"
              name="search"
              onChange={search}
            />
          </Stack>
        </div>

        <Stack direction="row" spacing={2}>
          <Button
            className="upload-button"
            startIcon={<UploadIcon />}
            variant="contained"
            onClick={handleClickOpen}
            id="upload-btn"
          >
            Upload
          </Button>
          <Box>
            <Dialog open={open} onClose={handleClose}>
              <DialogTitle className="dialog">
                Upload Video
                <IconButton
                  aria-label="close"
                
                  onClick={handleClose}
                  sx={{
                    position: "absolute",
                    right: 8,
                    top: 8,
                    color: (theme) => theme.palette.grey[500],
                  }}
                >
                  <CloseIcon />
                </IconButton>
              </DialogTitle>
              <DialogContent className="dialog">
                <TextField
                  autoFocus
                  margin="dense"
                  id="video-link"
                  label="Video Link"
                  type="text"
                  helperText="This Link will be used to derive the video"
                  fullWidth
                  name="videoLink"
                  variant="outlined"
                  value={value.videoLink}
                  onChange={handleChange}
                />
                <TextField
                  autoFocus
                  margin="dense"
                  id="thunmbnail"
                  label="Tumbnail Image Link"
                  type="text"
                  helperText="This link will be used to preview the thumbnail image"
                  fullWidth
                  variant="outlined"
                  name="previewImage"
                  value={value.previewImage}
                  onChange={handleChange}
                />
                <TextField
                  autoFocus
                  margin="dense"
                  id="title"
                  label="Title"
                  type="text"
                  helperText="The title will be representative text of video"
                  fullWidth
                  variant="outlined"
                  name="title"
                  value={value.title}
                  onChange={handleChange}
                />

                <FormControl fullWidth margin="dense">
                  <InputLabel>Genre</InputLabel>
                  <Select
                    id="genre"
                    label="genre"
                    style={{ color: "white" }}
                    value={value.genre}
                    onChange={handleChange}
                    name="genre"
                  >
                    <MenuItem value="All">All Genre</MenuItem>
                    <MenuItem value="Education">Education</MenuItem>
                    <MenuItem value="Sports">Sports</MenuItem>
                    <MenuItem value="Comedy">Comedy</MenuItem>
                    <MenuItem value="Lifestyle">Lifestyle</MenuItem>
                  </Select>
                  <FormHelperText>
                    Genre will help in categorizing the video
                  </FormHelperText>
                </FormControl>
                <FormControl fullWidth margin="dense">
                  <InputLabel>Suitable age group for clip</InputLabel>
                  <Select
                    id="age"
                    label="suitable age group for the clip"
                    style={{ color: "white" }}
                    value={value.contentRating}
                    onChange={handleChange}
                    name="contentRating"
                  >
                    <MenuItem value="All">All</MenuItem>
                    <MenuItem value="7+">7+</MenuItem>
                    <MenuItem value="12+">12+</MenuItem>
                    <MenuItem value="16+">16+</MenuItem>
                    <MenuItem value="18+">18+</MenuItem>
                  </Select>
                  <FormHelperText>
                    This will be used to filter videos on age group suitability
                  </FormHelperText>
                </FormControl>

                <TextField
                  id="date"
                  label="Release Date"
                  type="date"
                  data-date-format={new Date("yyyy-mm-dd").toLocaleString(
                    "en-us",
                    { month: "short", year: "numeric" }
                  )}
                  value={value.releaseDate}
                  onChange={handleChange}
                  name="releaseDate"
                  autoFocus
                  margin="dense"
                  fullWidth
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </DialogContent>
              <DialogActions className="dialog">
                <Button className="cancel-button" id="upload-btn-cancel" onClick={handleClose}>
                  Cancel
                </Button>
                <Button
                  className="submit-button"
                  id="upload-btn-submit"
                  variant="contained"
                  onClick={handleClose}
                >
                  Upload video
                </Button>
              </DialogActions>
            </Dialog>
          </Box>
        </Stack>
      </Box>
    </Box>
  )
}
export default Header
