import React, { useState } from 'react'
import './test.css'
const Test = () => {

  const [name, setName] = useState('');
  const [artists, setArtists] = useState([]);
  console.log(artists)

  const add = ()=>{
    if (name) {
      setArtists(artists, artists.push(name));
      setName("");
    } 
  }
  const remove=(e)=>{
    const name = e;
    setArtists(
      artists.filter(function (item) {
        return item !== name;
      })
    );
    console.log(e)
  }
  return (
    <>
      <h1>Inspiring sculptors:</h1>
      <input
        value={name}
        onChange={e => setName(e.target.value)}
      />
      <button onClick={add}>Add</button>
      <ul>
        {artists.map((val,index) => (
          <li key={index}>{val}
          <button onClick={()=>remove(val)}>Remove</button>
          </li>
        ))}
      </ul>
    </>
  );
}

export default Test
