import { createSlice } from "@reduxjs/toolkit";
const initialState = {
  contentRating: "",
  genre: "",
  previewImage: "",
  releaseDate: "",
  title: "",
  videoLink: "",
  viewCount: 0,
  votes: {
    upVotes: 0,
    downVotes: 0,
  },
  //_id: ""]
};
export const videoSlice = createSlice({
  name: "vote",
  initialState,
  reducers: {
    changeVideo: (state, action) => {
      state.video = action.payload;
    },
    getVideo: (state, action) => {
      state.videos = action.payload;
    },
    changeUpvoteIncrement: (state) => {
      state.votes.upVotes += 1;
    },
    changeUpvoteDecrement: (state) => {
      state.votes.upVotes -= 1;
    },
    changeDownvoteIncrement: (state) => {
      state.votes.downVotes += 1;
    },
    changeDownvoteDecrement: (state) => {
      state.votes.downVotes -= 1;
    },
  },
});
export const {
  changeVideo,
  changeUpvoteIncrement,
  changeUpvoteDecrement,
  changeDownvoteDecrement,
  changeDownvoteIncrement,
  getVideo,
} = videoSlice.actions;
export default videoSlice.reducer;
