import { createSlice } from "@reduxjs/toolkit";
const initialState = {
    title:" ",
    ingridients:"",
    steps:"",
    calories:"",
    prepTime:"",
    image:"",
    foodType:"",
    mealType:"",
};
export const recipeSlice = createSlice({
  name: "recipe",
  initialState,
  reducers: {
    getRecipe:(state,action)=>{
        state.recipe = action.payload
        console.log(state.recipe)
    },
  },
});
export const {
getRecipe,
} = recipeSlice.actions;
export default recipeSlice.reducer;
