import { Box, Button } from "@mui/material"
import './header.css'
import FilterAltIcon from '@mui/icons-material/FilterAlt';
import Select from "react-select";

import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { changeDurationOption, changeOption, changeRecipe, getRecipes } from "../features/recipeSlice";
import { config } from "../App";
import HandleFilter from "./filter";
const Header = () => {
  const { meal } = useSelector((state) => state.meal);
  const { duration } = useSelector((state) => state.duration)

  const dispatch = useDispatch();

  let options = [{ value: "breakfast", label: "Breakfast" },
  { value: "lunch", label: "Lunch" },
  { value: "dinner", label: "Dinner" }]

  let optionsDuration = [{ value: "5-10", label: "5-10" },
  { value: "20-30", label: "20-30" },
  { value: "1", label: "1 Hour" },
  { value: "1+", label: "More than 1 Hour" }
  ]

  const handleClear = () => {
    dispatch(changeOption([]))
  };

  const handleClearDuration = () => {
    dispatch(changeDurationOption([]))
  };
  const performMealFilter = async (e, text) => {
    // console.log("onchange")
    dispatch(changeOption(e))
    let response = {};
    let errored = false;

    try {
      response = await (
        await fetch(`${config.endpoint}/recipe?meal=${e.value}`, {
          method: "POST",
        })
      ).json();
    } catch (e) {
      errored = true;
      return errored;
    }
    // console.log(response)
    dispatch(getRecipes(response));
    // return re;
  };

  return (
    <Box>
      <Box className="filter-btn-box">
        {/* <Button className="filter-btn" startIcon={<FilterAltIcon/>} onClick={handleClick}>
                Filter
            </Button> */}
        <Button className="filter-bar" onClick={<HandleFilter/>}>
        
        </Button>

      </Box>
    </Box>
  )
}
export default Header
