import { Box, Button } from "@mui/material"
import './header.css'
import FilterAltIcon from '@mui/icons-material/FilterAlt';
import Select from "react-select";

import { useRef, useState } from "react";
const Header=()=>{

    const [selected, setSelected] = useState([]);
    const selectInputRef = useRef();
    const [options, setOptions] = useState([
        { value: "breakfast", label: "Breakfast" },
        { value: "lunch", label: "Lunch" },
        { value: "dinner", label: "Dinner" }
      ]);

      
    const handleClear = () => {
        // setOptions((currentOptions) => currentOptions.filter((currentOption) => !selected.includes(currentOption)));
        // setSelected([]);
        selectInputRef.current.select.clearValue();

      };
      
    return(
        <Box>
            <Box className="filter-btn-box">
            {/* <Button className="filter-btn" startIcon={<FilterAltIcon/>} onClick={handleClick}>
                Filter
            </Button> */}
              <div className="filter-bar">
                    <div className="filter-bar-tile">
                        <label htmlFor="duration">Filters:</label>
                    </div>
                    {/* <!-- Duration filter (select one) --> */}
                    <div className="filter-bar-tile">
                        {/* <select className="form-control" name="duration" id="duration-select"
                            onchange="selectDuration(event)">
                            <option disabled selected value="">
                                Filter by Duration (Hours)
                            </option>
                            <option value="0-2">5-10 Min</option>
                            <option value="2-6">20-30 Min</option>
                            <option value="6-12">1 Hours</option>
                            <option value="12-99">1+ Hours</option>
                        </select> */}
                              <Select  ref={selectInputRef} value={selected} options={[
        { value: "breakfast", label: "Breakfast" },
        { value: "lunch", label: "Lunch" },
        { value: "dinner", label: "Dinner" }
      ]} onChange={setSelected} />

                        <div className="ms-3" style={{color: "#b13d2f", cursor: "pointer"}} onClick={handleClear}>
                            Clear
                        </div>
                    </div>
                    {/* <!-- Category filter (select one or many) --> */}
                    <div className="filter-bar-tile d-flex align-items-center">
                        <select className="form-control" id="category-select" onchange="selectCategory(event)">
                            <option disabled selected value="">Meal Type</option>
                            <option value="Cycling">Lunch</option>
                            <option value="Hillside">Dinner</option>
                            <option value="Beaches">Breakfast</option>
                          
                        </select>
                        <div className="ms-3" style={{color: "#b13d2f", cursor: "pointer"}} onClick="clearCategory(event)">
                            Clear
                        </div>
                    </div>
                </div>
                
            </Box>
        </Box>
    )
}
export default Header
