import { Box } from "@mui/material";
import { useEffect } from "react";
import { useParams } from "react-router-dom";
import './recipe.css'
import { config } from "../App";
import { useDispatch, useSelector } from "react-redux";
import { changeRecipe } from "../features/recipeSlice";
const Recipe = ()=>{
    let { recipeId } = useParams();
    const dispatch = useDispatch();
    const { recipe } = useSelector((state) => state.recipe);
    const performApiCall = async () => {
        let response = {};
        let errored = false;
        try {
          response = await (
            await fetch(`${config.endpoint}/recipe/${recipeId}`, {
              method: "GET",
            })
          ).json();
        } catch (e) {
          errored = true;
          return errored;
        }
    
        dispatch(changeRecipe(response));
        return recipe;
      };

      
      useEffect(() => {

        performApiCall();
      }, []);

      return (
        <Box>
          <div className="container">

<span className="border tl"></span>
<span className="border tr"></span>
<span className="border bl"></span>
<span className="border br"></span>


</div>
        </Box>
      )
}
export default Recipe
