import {
    Box,
    Card,
    CardContent,
    CardMedia,
    Typography,
    Grid,
    Stack,
  } from "@mui/material";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { config } from "../App";
import WhatshotOutlinedIcon from '@mui/icons-material/WhatshotOutlined';
import ScheduleOutlinedIcon from '@mui/icons-material/ScheduleOutlined';
import './homepage.css'
const Homepage=()=>{
   const dispatch = useDispatch();
   const { recipe } = useSelector((state) => state.recipe);
//  const [recipe,setRecipe] = useState()
  console.log(recipe)
  useEffect(() => {
   getRecipe();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);
  const getRecipe = async () => {
    let response = {};
    let errored = false;

    try {
      response = await (
        await fetch(`${config.endpoint}/recipe`, {
          method: "GET",
        })
      ).json();
    } catch (e) {
      errored = true;
      return errored;
    }
    setRecipe(response)

    // dispatch(getRecipe(response));
  };
  console.log("response",recipe)
  return (
        <Box style={{display:"flex",justifyContent:"center"}}>
             {typeof recipe !== "undefined" && (
          <Grid container spacing={2} sx={{ m: 2 }} alignItems="stretch" justifyContent="center" style={{width:"80vw"}}>
            {recipe.map((ele) => {
              return (
                <Grid item  xs={8} md={3} key={ele._id} className="recipe-grid">
                  {/* <Link to={"/video/" + ele._id} className="video-tile-link"> */}
                    <Card className="recipe-tile" sx={{ maxWidth: 345 }}>
                      <CardMedia
                        // style={{ height: "70%" }}
                        sx={{ height: 200 }}
                        component="img"
                        image={ele.image}
                        alt={ele.title}
                      />
                      <CardContent>
                        <Box>
                        <Typography
                          component="h5"
                          variant="h5"
                          className="nheadingrs"
                        >
                          {ele.title}
                          <span className="rsvegnonveg veg"><span className="veg"></span></span>
                        </Typography>
                       

                        </Box>
                        <Box style={{display:"flex",justifyContent:'space-between',padding:"10px"}}>
                        <Stack direction="row" alignItems="center">
                          <WhatshotOutlinedIcon style={{height:"20px" ,color:"red"}} />
                          <Typography
                            variant="subtitle1"
                            color="textSecondary"
                          >
                        {ele.calories}

                          </Typography>
                      </Stack>
                      <Stack direction="row" alignItems="center" gap="2px">
                        <ScheduleOutlinedIcon style={{height:"20px"}}/>
                          <Typography
                            variant="subtitle1"
                            color="textSecondary"
                          >
                            {ele.prepTime}
                          </Typography>
                        </Stack>
                          </Box>
                      </CardContent>
                    </Card>
                  {/* </Link> */}
                </Grid>
              );
            })}
          </Grid>
          )}

      </Box>
    )
}
export default Homepage
